function merge(arr1, arr2) {
  // Initialize an empty array to store the result
  let mergedArray = [];

  // Pointers for both arrays
  let i = 0; // pointer for arr1
  let j = 0; // pointer for arr2

  // Loop until elements remain in both arrays
  while (i < arr1.length && j < arr2.length) {
    if (arr1[i] < arr2[j]) {
      // If arr1 element is smaller, push it to mergedArray
      mergedArray.push(arr1[i]);
      i++;
    } else {
      // If arr2 element is smaller or equal, push it to mergedArray
      mergedArray.push(arr2[j]);
      j++;
    }
  }

  // If elements remain in arr1, push them
  while (i < arr1.length) {
    mergedArray.push(arr1[i]);
    i++;
  }

  // If elements remain in arr2, push them
  while (j < arr2.length) {
    mergedArray.push(arr2[j]);
    j++;
  }

  return mergedArray;
}

// Example
const res = merge([3, 27, 38, 43], [9, 10, 17, 82]);
console.log(res); // Output: [3, 9, 10, 17, 27, 38, 43, 82]
