function compressString(input) {
    let result = "";
    let count = 1;

    for (let i = 1; i <= input.length; i++) {
        // Compare current char with previous char
        if (input[i] === input[i - 1]) {
            count++;
        } else {
            // Append previous char and its count
            result += input[i - 1] + count;
            count = 1;
        }
    }

    return result;
}

// Test
