/**
 * Validates an IPv4 address without using regular expressions
 * @param {string} ipAddress - The IP address to validate
 * @returns {boolean} - Returns true if the IP address is valid, false otherwise
 */
function isValidIPv4(ipAddress) {
  // Check if input is a string
  if (typeof ipAddress !== 'string') {
    return false;
  }

  // Split the IP address by dots
  const octets = ipAddress.split('.');

  // A valid IPv4 address must have exactly 4 parts (octets)
  if (octets.length !== 4) {
    return false;
  }

  // Check each octet
  for (const octet of octets) {
    // Check if the octet is empty
    if (octet.length === 0) {
      return false;
    }

    // Check for leading zeros (except for single '0')
    if (octet.length > 1 && octet.startsWith('0')) {
      return false;
    }

    // Check if the octet contains only digits
    for (const char of octet) {
      if (char < '0' || char > '9') {
        return false;
      }
    }

    // Convert the octet to number and validate range (0-255)
    const num = parseInt(octet, 10);
    if (isNaN(num) || num < 0 || num > 255) {
      return false;
    }
  }

  return true;
}

// Example usage:
console.log(isValidIPv4('192.168.1.1'));       // true
console.log(isValidIPv4('255.255.255.255'));   // true
console.log(isValidIPv4('0.0.0.0'));           // true
console.log(isValidIPv4('256.256.256.256'));   // false (values > 255)
console.log(isValidIPv4('192.168.1'));         // false (not enough octets)
console.log(isValidIPv4('192.168.1.1.1'));     // false (too many octets)
console.log(isValidIPv4('192.168.01.1'));      // false (leading zeros)
console.log(isValidIPv4('192.168.1.abc'));     // false (not numeric)
console.log(isValidIPv4('...'));               // false (empty octets)
