function isValidParentheses(s) {
  const stack = [];
  const map = { '(': ')', '{': '}', '[': ']' };

  for (let char of s) {
    if (map[char]) {
      stack.push(char);
    } else {
      const last = stack.pop();
      if (char !== map[last]) return false;
    }
  }
  return stack.length === 0;
}

