function rotateArray(arr, k) {
  k = k % arr.length;
  // Splice modifies original array or use slice for new array
  // Method 1: Pop and Unshift (Slow for large k)
  // Method 2: Slice and Concat
  const end = arr.slice(arr.length - k);
  const start = arr.slice(0, arr.length - k);
  return end.concat(start);
}

