function moveZeroes(nums) {
  let index = 0;
  // Move non-zero elements to front
  for (let i = 0; i < nums.length; i++) {
    if (nums[i] !== 0) {
      nums[index] = nums[i];
      index++;
    }
  }
  // Fill remaining with zeros
  for (let i = index; i < nums.length; i++) {
    nums[i] = 0;
  }
  return nums;
}

